import jwtDecode from 'jut-decode';

const Protected = () => {
    const token = localStorage.getItem('token');
    if (!token) {
    return <Redirect to="/login"
    />;
    }
    try {
    // decode JWT to get user information
    const user = jwtDecode(token);
    return <div>Welcome {user .username}!</div>;
    } catch (error) {
        console.log(err);
    }
}
