import React, { Component } from "react";
import { Button, Form, Label, Input } from 'reactstrap';
import { Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';

class Enter extends Component {
    constructor(props) {
        super(props);
        // this.state = {
        //     user: null
