        // // event.preventDefault();

        fetch('http://localhost:5001/add-user', {
            method: 'post',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                first_name: 'Hello',
            }),
        });

    }

    render() {
        return (
            <div>
                <Breadcrumb>
                    <BreadcrumbItem active>Enter User</BreadcrumbItem>
                    <BreadcrumbItem> <Link to="/users">Users</Link> </BreadcrumbItem>
                </Breadcrumb>

                <Form onSubmit={this.handleSubmit}>
                    <Input type="text" name="username" innerRef={(input)=> this.username = input}/>
                    <Button type="submit">Add</Button>
                </Form>
            </div>
        )
    }

}

export default Enter;
