import React, { Component } from "react";
import { Button, Form, Label, Input } from 'reactstrap';
import { Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';

class Enter extends Component {
    constructor(props) {
        super(props);
        // this.state = {
        //     user: null
        // }
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleSubmit() {
        console.log('value', this.username.value);
        const name = this.username.value;
        fetch('http://localhost:5001/add-user', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(name),
            credentials: "same-origin"
            })
            .then((res) => res.json())
            .catch((error) => console.error('Error:', error))
    }

    render() {
        return (
            <div>
                <Breadcrumb>
                    <BreadcrumbItem active>Enter User</BreadcrumbItem>
                    <BreadcrumbItem> <Link to="/users">Users</Link> </BreadcrumbItem>
                </Breadcrumb>

                <Form onSubmit={this.handleSubmit}>
                    <Input type="text" name="username" innerRef={(input)=> this.username = input}/>
                    <Button type="submit">Add</Button>
                </Form>
            </div>
        )
    }

}

export default Enter;
